public class Botao
{
    public event Action<string>? Clicado;
    public void Clicar() => Clicado?.Invoke("clicou");
}

public class Solution
{
    public static string Teste()
    {
        string resultado = "";
        var btn = new Botao();
        btn.Clicado += msg => resultado = msg;
        btn.Clicar();
        return resultado;
    }
}

