using System;
using System.Collections.Generic;
using System.Linq;

public class TestRunner
{
    public static List<TestResult> RunTests()
    {
        var results = new List<TestResult>();
        try { bool p = { var r = Solution.Transformar(new List<int>{5,2,8,1,4}); return r.Count == 2 && r[0] == "8 é par" && r[1] == "4 é par"; }; results.Add(new TestResult { Pass = p, Message = p ? "Transformado!" : "Incorreto" }); } catch (Exception ex) { results.Add(new TestResult { Pass = false, Message = "Erro: " + ex.Message }); }
        return results;
    }
}

public class TestResult { public bool Pass { get; set; } public string Message { get; set; } }
