public class Config
{
    public string Nome { get; init; } = "";
    public int Valor { get; init; }
}

public class Solution
{
    public static string Teste()
    {
        var c = new Config { Nome = "teste", Valor = 42 };
        return $"{c.Nome}:{c.Valor}";
    }
}

