public class Vetor
{
    public double X { get; set; }
    public double Y { get; set; }
    // Sobrecarregue operator +(Vetor a, Vetor b)
}

public class Solution
{
    public static double Teste()
    {
        var v = new Vetor{X=1,Y=2} + new Vetor{X=3,Y=4};
        return v.X + v.Y;
    }
}

