public class Recurso : IDisposable
{
    public bool Aberto { get; private set; } = true;
    public void Dispose() { Aberto = false; }
}

public class Solution
{
    public static bool Teste()
    {
        Recurso r;
        using (r = new Recurso()) { /* usa recurso */ }
        return !r.Aberto;
    }
}

