public class Ponto
{
    public double X { get; set; }
    public double Y { get; set; }
    public void Deconstruct(out double x, out double y) { x = X; y = Y; }
}

public class Solution
{
    public static double Teste()
    {
        var p = new Ponto { X = 3, Y = 4 };
        var (x, y) = p;
        return x + y;
    }
}

