using System;
using System.Collections.Generic;
using System.Linq;

public class TestRunner
{
    public static List<TestResult> RunTests()
    {
        var results = new List<TestResult>();
        try { var r = Solution.Cumprimentar(null); bool p = r == "Olá, Visitante!"; results.Add(new TestResult { Pass = p, Message = p ? "Null ok!" : "Incorreto: " + r }); } catch (Exception ex) { results.Add(new TestResult { Pass = false, Message = "Erro: " + ex.Message }); }
        try { var r = Solution.Cumprimentar("Ana"); bool p = r == "Olá, Ana!"; results.Add(new TestResult { Pass = p, Message = p ? "Ana ok!" : "Incorreto: " + r }); } catch (Exception ex) { results.Add(new TestResult { Pass = false, Message = "Erro: " + ex.Message }); }
        return results;
    }
}

public class TestResult { public bool Pass { get; set; } public string Message { get; set; } }
