using System;
using System.Collections.Generic;
using System.Linq;

public class TestRunner
{
    public static List<TestResult> RunTests()
    {
        var results = new List<TestResult>();
        try { var r = Solution.Classificar(8); bool p = r == "criança"; results.Add(new TestResult { Pass = p, Message = p ? "8=criança!" : "Incorreto: " + r }); } catch (Exception ex) { results.Add(new TestResult { Pass = false, Message = "Erro: " + ex.Message }); }
        try { var r = Solution.Classificar(15); bool p = r == "adolescente"; results.Add(new TestResult { Pass = p, Message = p ? "15=adolescente!" : "Incorreto: " + r }); } catch (Exception ex) { results.Add(new TestResult { Pass = false, Message = "Erro: " + ex.Message }); }
        try { var r = Solution.Classificar(25); bool p = r == "adulto"; results.Add(new TestResult { Pass = p, Message = p ? "25=adulto!" : "Incorreto: " + r }); } catch (Exception ex) { results.Add(new TestResult { Pass = false, Message = "Erro: " + ex.Message }); }
        return results;
    }
}

public class TestResult { public bool Pass { get; set; } public string Message { get; set; } }
