using System;
using System.Collections.Generic;
using System.Linq;

public class TestRunner
{
    public static List<TestResult> RunTests()
    {
        var results = new List<TestResult>();
        try { var r = Solution.Fatorial(5); bool p = r == 120L; results.Add(new TestResult { Pass = p, Message = p ? "5!=120!" : "Incorreto: " + r }); } catch (Exception ex) { results.Add(new TestResult { Pass = false, Message = "Erro: " + ex.Message }); }
        try { var r = Solution.Fatorial(0); bool p = r == 1L; results.Add(new TestResult { Pass = p, Message = p ? "0!=1!" : "Incorreto: " + r }); } catch (Exception ex) { results.Add(new TestResult { Pass = false, Message = "Erro: " + ex.Message }); }
        return results;
    }
}

public class TestResult { public bool Pass { get; set; } public string Message { get; set; } }
