using System;
using System.Collections.Generic;
using System.Linq;

public class TestRunner
{
    public static List<TestResult> RunTests()
    {
        var results = new List<TestResult>();
        try { var r = Solution.Fibonacci(0); bool p = r == 0; results.Add(new TestResult { Pass = p, Message = p ? "F(0)=0!" : "Incorreto: " + r }); } catch (Exception ex) { results.Add(new TestResult { Pass = false, Message = "Erro: " + ex.Message }); }
        try { var r = Solution.Fibonacci(6); bool p = r == 8; results.Add(new TestResult { Pass = p, Message = p ? "F(6)=8!" : "Incorreto: " + r }); } catch (Exception ex) { results.Add(new TestResult { Pass = false, Message = "Erro: " + ex.Message }); }
        return results;
    }
}

public class TestResult { public bool Pass { get; set; } public string Message { get; set; } }
