using System;
using System.Collections.Generic;
using System.Linq;

public class TestRunner
{
    public static List<TestResult> RunTests()
    {
        var results = new List<TestResult>();
        try { bool p = Math.Abs(Solution.CelsiusParaFahrenheit(0) - 32) < 0.01; results.Add(new TestResult { Pass = p, Message = p ? "0°C=32°F!" : "Incorreto" }); } catch (Exception ex) { results.Add(new TestResult { Pass = false, Message = "Erro: " + ex.Message }); }
        try { bool p = Math.Abs(Solution.CelsiusParaFahrenheit(100) - 212) < 0.01; results.Add(new TestResult { Pass = p, Message = p ? "100°C=212°F!" : "Incorreto" }); } catch (Exception ex) { results.Add(new TestResult { Pass = false, Message = "Erro: " + ex.Message }); }
        return results;
    }
}

public class TestResult { public bool Pass { get; set; } public string Message { get; set; } }
