using System;
using System.Collections.Generic;
using System.Linq;

public class TestRunner
{
    public static List<TestResult> RunTests()
    {
        var results = new List<TestResult>();
        try { var r = Solution.SeguroDiv(10, 2); bool p = r == (int?)5; results.Add(new TestResult { Pass = p, Message = p ? "10/2=5!" : "Incorreto: " + r }); } catch (Exception ex) { results.Add(new TestResult { Pass = false, Message = "Erro: " + ex.Message }); }
        try { bool p = Solution.SeguroDiv(10, 0) == null; results.Add(new TestResult { Pass = p, Message = p ? "Div por 0 = null!" : "Incorreto" }); } catch (Exception ex) { results.Add(new TestResult { Pass = false, Message = "Erro: " + ex.Message }); }
        return results;
    }
}

public class TestResult { public bool Pass { get; set; } public string Message { get; set; } }
