using System;
using System.Collections.Generic;
using System.Linq;

public class TestRunner
{
    public static List<TestResult> RunTests()
    {
        var results = new List<TestResult>();
        try { var r = Solution.CorParaHex(Cor.Vermelho); bool p = r == "#FF0000"; results.Add(new TestResult { Pass = p, Message = p ? "Vermelho ok!" : "Incorreto: " + r }); } catch (Exception ex) { results.Add(new TestResult { Pass = false, Message = "Erro: " + ex.Message }); }
        try { var r = Solution.CorParaHex(Cor.Azul); bool p = r == "#0000FF"; results.Add(new TestResult { Pass = p, Message = p ? "Azul ok!" : "Incorreto: " + r }); } catch (Exception ex) { results.Add(new TestResult { Pass = false, Message = "Erro: " + ex.Message }); }
        return results;
    }
}

public class TestResult { public bool Pass { get; set; } public string Message { get; set; } }
