using System;
using System.Collections.Generic;
using System.Linq;

public class TestRunner
{
    public static List<TestResult> RunTests()
    {
        var results = new List<TestResult>();
        try { bool p = Solution.Pares(new List<int>{1,2,3,4,5,6}).SequenceEqual(new[]{2,4,6}); results.Add(new TestResult { Pass = p, Message = p ? "Pares filtrados!" : "Incorreto" }); } catch (Exception ex) { results.Add(new TestResult { Pass = false, Message = "Erro: " + ex.Message }); }
        return results;
    }
}

public class TestResult { public bool Pass { get; set; } public string Message { get; set; } }
