public class TestRunner
{
    public static List<TestResult> RunTests()
    {
        var results = new List<TestResult>();
        try { bool p = Solution.Ordenar(new List<string>{"Carlos","Ana","Bruna"}).SequenceEqual(new[]{"Ana","Bruna","Carlos"}); results.Add(new TestResult { Pass = p, Message = p ? "Ordenado!" : "Incorreto" }); } catch (Exception ex) { results.Add(new TestResult { Pass = false, Message = "Erro: " + ex.Message }); }
        return results;
    }
}

public class TestResult { public bool Pass { get; set; } public string Message { get; set; } }
