using System;
using System.Collections.Generic;
using System.Linq;

public class TestRunner
{
    public static List<TestResult> RunTests()
    {
        var results = new List<TestResult>();
        try { bool p = { var r = Solution.Calcular(new List<int>{1,2,3,4,5}); return r.soma == 15 && Math.Abs(r.media - 3.0) < 0.01; }; results.Add(new TestResult { Pass = p, Message = p ? "Soma=15, Média=3!" : "Incorreto" }); } catch (Exception ex) { results.Add(new TestResult { Pass = false, Message = "Erro: " + ex.Message }); }
        return results;
    }
}

public class TestResult { public bool Pass { get; set; } public string Message { get; set; } }
