public class TestRunner
{
    public static List<TestResult> RunTests()
    {
        var results = new List<TestResult>();
        try { bool p = { var r = Solution.ContarPorInicial(new List<string>{"Ana","Alice","Bob"}); return r["A"] == 2 && r["B"] == 1; }; results.Add(new TestResult { Pass = p, Message = p ? "Agrupado!" : "Incorreto" }); } catch (Exception ex) { results.Add(new TestResult { Pass = false, Message = "Erro: " + ex.Message }); }
        return results;
    }
}

public class TestResult { public bool Pass { get; set; } public string Message { get; set; } }
