public class Pessoa
{
    // Propriedades Nome (string) e Idade (int)
    // Método Apresentar() retorna "Sou {Nome}, tenho {Idade} anos"
}

public class Solution
{
    public static string Teste()
    {
        var p = new Pessoa { Nome = "Ana", Idade = 25 };
        return p.Apresentar();
    }
}

