public class Animal
{
    public string Nome { get; set; }
    public virtual string Falar() => $"{Nome} faz som";
}

public class Cachorro : Animal
{
    // Override Falar() para retornar "{Nome} diz: Au au!"
}

public class Solution
{
    public static string Teste() => new Cachorro { Nome = "Rex" }.Falar();
}

