public abstract class Forma
{
    public abstract double Area();
}

public class Circulo : Forma
{
    public double Raio { get; set; }
    // Override Area() = π * r²
}

public class Solution
{
    public static double Teste() => new Circulo { Raio = 5 }.Area();
}

