using System;
using System.Collections.Generic;
using System.Linq;

public class TestRunner
{
    public static List<TestResult> RunTests()
    {
        var results = new List<TestResult>();
        try { bool p = { var r = Solution.ContarPalavras("oi oi tchau"); return r["oi"] == 2 && r["tchau"] == 1; }; results.Add(new TestResult { Pass = p, Message = p ? "Contagem ok!" : "Incorreto" }); } catch (Exception ex) { results.Add(new TestResult { Pass = false, Message = "Erro: " + ex.Message }); }
        return results;
    }
}

public class TestResult { public bool Pass { get; set; } public string Message { get; set; } }
