using System;
using System.Collections.Generic;
using System.Linq;

public class TestRunner
{
    public static List<TestResult> RunTests()
    {
        var results = new List<TestResult>();
        try { bool p = Solution.Unicos(new List<int>{1,2,2,3,3}).SequenceEqual(new[]{1,2,3}); results.Add(new TestResult { Pass = p, Message = p ? "Únicos!" : "Incorreto" }); } catch (Exception ex) { results.Add(new TestResult { Pass = false, Message = "Erro: " + ex.Message }); }
        return results;
    }
}

public class TestResult { public bool Pass { get; set; } public string Message { get; set; } }
