using System;
using System.Collections.Generic;
using System.Linq;

public class TestRunner
{
    public static List<TestResult> RunTests()
    {
        var results = new List<TestResult>();
        try { var r = Solution.DivisaoSegura(10, 2); bool p = r == "5"; results.Add(new TestResult { Pass = p, Message = p ? "10/2=5!" : "Incorreto: " + r }); } catch (Exception ex) { results.Add(new TestResult { Pass = false, Message = "Erro: " + ex.Message }); }
        try { var r = Solution.DivisaoSegura(10, 0); bool p = r == "Erro: divisão por zero"; results.Add(new TestResult { Pass = p, Message = p ? "Erro capturado!" : "Incorreto: " + r }); } catch (Exception ex) { results.Add(new TestResult { Pass = false, Message = "Erro: " + ex.Message }); }
        return results;
    }
}

public class TestResult { public bool Pass { get; set; } public string Message { get; set; } }
