using System;
using System.Collections.Generic;
using System.Linq;

public class TestRunner
{
    public static List<TestResult> RunTests()
    {
        var results = new List<TestResult>();
        try { bool p = { try { Solution.ValidarIdade(-1); return false; } catch (IdadeInvalidaException) { return true; } }; results.Add(new TestResult { Pass = p, Message = p ? "Exceção lançada!" : "Exceção não lançada" }); } catch (Exception ex) { results.Add(new TestResult { Pass = false, Message = "Erro: " + ex.Message }); }
        try { bool p = { try { Solution.ValidarIdade(25); return true; } catch { return false; } }; results.Add(new TestResult { Pass = p, Message = p ? "25 é válido!" : "Não deveria lançar" }); } catch (Exception ex) { results.Add(new TestResult { Pass = false, Message = "Erro: " + ex.Message }); }
        return results;
    }
}

public class TestResult { public bool Pass { get; set; } public string Message { get; set; } }
