public struct Ponto
{
    public double X { get; set; }
    public double Y { get; set; }
    // Método Distancia(Ponto outro)
}

public class Solution
{
    public static double Teste()
    {
        var p1 = new Ponto { X = 0, Y = 0 };
        var p2 = new Ponto { X = 3, Y = 4 };
        return p1.Distancia(p2);
    }
}

