public class Produto
{
    private double _preco;
    public double Preco
    {
        get => _preco;
        set
        {
            // Lance ArgumentException se value < 0
            _preco = value;
        }
    }
}

public class Solution
{
    public static bool Teste()
    {
        try { new Produto { Preco = -1 }; return false; }
        catch (ArgumentException) { return true; }
    }
}

