using System;
using System.Collections.Generic;
using System.Linq;

public class TestRunner
{
    public static List<TestResult> RunTests()
    {
        var results = new List<TestResult>();
        try { var r = Solution.Somar(1, 2, 3); bool p = r == 6; results.Add(new TestResult { Pass = p, Message = p ? "1+2+3=6!" : "Incorreto: " + r }); } catch (Exception ex) { results.Add(new TestResult { Pass = false, Message = "Erro: " + ex.Message }); }
        try { var r = Solution.Somar(10); bool p = r == 10; results.Add(new TestResult { Pass = p, Message = p ? "10 ok!" : "Incorreto: " + r }); } catch (Exception ex) { results.Add(new TestResult { Pass = false, Message = "Erro: " + ex.Message }); }
        return results;
    }
}

public class TestResult { public bool Pass { get; set; } public string Message { get; set; } }
