using System;
using System.Collections.Generic;
using System.Linq;

public class TestRunner
{
    public static List<TestResult> RunTests()
    {
        var results = new List<TestResult>();
        try { var r = Solution.FormatarNome("Ana", "Silva"); bool p = r == "Ana Silva"; results.Add(new TestResult { Pass = p, Message = p ? "Sem título ok!" : "Incorreto: " + r }); } catch (Exception ex) { results.Add(new TestResult { Pass = false, Message = "Erro: " + ex.Message }); }
        try { var r = Solution.FormatarNome("Ana", "Silva", titulo: "Dra."); bool p = r == "Dra. Ana Silva"; results.Add(new TestResult { Pass = p, Message = p ? "Com título ok!" : "Incorreto: " + r }); } catch (Exception ex) { results.Add(new TestResult { Pass = false, Message = "Erro: " + ex.Message }); }
        return results;
    }
}

public class TestResult { public bool Pass { get; set; } public string Message { get; set; } }
