using System;
using System.Collections.Generic;
using System.Linq;

public class TestRunner
{
    public static List<TestResult> RunTests()
    {
        var results = new List<TestResult>();
        try { var r = Solution.Descrever(42); bool p = r == "inteiro: 42"; results.Add(new TestResult { Pass = p, Message = p ? "Inteiro ok!" : "Incorreto: " + r }); } catch (Exception ex) { results.Add(new TestResult { Pass = false, Message = "Erro: " + ex.Message }); }
        try { var r = Solution.Descrever("oi"); bool p = r == "texto: oi"; results.Add(new TestResult { Pass = p, Message = p ? "Texto ok!" : "Incorreto: " + r }); } catch (Exception ex) { results.Add(new TestResult { Pass = false, Message = "Erro: " + ex.Message }); }
        try { var r = Solution.Descrever(null); bool p = r == "nulo"; results.Add(new TestResult { Pass = p, Message = p ? "Nulo ok!" : "Incorreto: " + r }); } catch (Exception ex) { results.Add(new TestResult { Pass = false, Message = "Erro: " + ex.Message }); }
        return results;
    }
}

public class TestResult { public bool Pass { get; set; } public string Message { get; set; } }
