public abstract class Veiculo
{
    public string Modelo { get; set; }
    public abstract int Rodas();
}

public class Carro : Veiculo
{
    // Rodas() retorna 4
}

public class Moto : Veiculo
{
    // Rodas() retorna 2
}

public class Solution
{
    public static int Teste() => new Carro { Modelo = "Civic" }.Rodas() + new Moto { Modelo = "CB" }.Rodas();
}

