function __runTests() {
    const results = [];
    try { const p = criarProxyValidado({}, {name:'string',age:'number'}); p.name = 'Ana'; results.push({ pass: p.name==='Ana', message: p.name==='Ana' ? 'Proxy ok!' : 'Incorreto' }); } catch(e) { results.push({ pass: false, message: 'Erro: ' + e.message }); }
    return results;
}
