function __runTests() {
    const results = [];
    try { const r = pipe(x => x*2, x => x+1)(3); results.push({ pass: r===7, message: r===7 ? 'pipe ok! (3*2)+1=7' : 'Incorreto: ' + r }); } catch(e) { results.push({ pass: false, message: 'Erro: ' + e.message }); }
    return results;
