function __runTests() {
    const results = [];
    try { const r = soma(2, 3); const exp = "5"; const pass = JSON.stringify(r) === JSON.stringify(exp); results.push({ pass, message: pass ? 'soma(2,3) = 5!' : 'soma(2,3) incorreto: esperado ' + JSON.stringify(exp) + ' obteve ' + JSON.stringify(r) }); } catch(e) { results.push({ pass: false, message: 'Erro: ' + e.message }); }
    try { const r = soma(-1, 1); const exp = "0"; const pass = JSON.stringify(r) === JSON.stringify(exp); results.push({ pass, message: pass ? 'soma(-1,1) = 0!' : 'soma(-1,1) incorreto: esperado ' + JSON.stringify(exp) + ' obteve ' + JSON.stringify(r) }); } catch(e) { results.push({ pass: false, message: 'Erro: ' + e.message }); }
    try { const r = soma(0, 0); const exp = "0"; const pass = JSON.stringify(r) === JSON.stringify(exp); results.push({ pass, message: pass ? 'soma(0,0) = 0!' : 'soma(0,0) incorreto: esperado ' + JSON.stringify(exp) + ' obteve ' + JSON.stringify(r) }); } catch(e) { results.push({ pass: false, message: 'Erro: ' + e.message }); }
    return results;
}
