function __runTests() {
    const results = [];
    try { const r = contemPalavra('Hello World', 'World'); const exp = "true"; const pass = JSON.stringify(r) === JSON.stringify(exp); results.push({ pass, message: pass ? 'World encontrado!' : 'Incorreto: esperado ' + JSON.stringify(exp) + ' obteve ' + JSON.stringify(r) }); } catch(e) { results.push({ pass: false, message: 'Erro: ' + e.message }); }
    try { const r = contemPalavra('Hello', 'xyz'); const exp = "false"; const pass = JSON.stringify(r) === JSON.stringify(exp); results.push({ pass, message: pass ? 'xyz não encontrado!' : 'Incorreto: esperado ' + JSON.stringify(exp) + ' obteve ' + JSON.stringify(r) }); } catch(e) { results.push({ pass: false, message: 'Erro: ' + e.message }); }
    return results;
}
