    try { const r = saudacao('João'); const exp = "'Ol\u00e1, Jo\u00e3o!'"; const pass = JSON.stringify(r) === JSON.stringify(exp); results.push({ pass, message: pass ? 'João ok!' : 'Incorreto: esperado ' + JSON.stringify(exp) + ' obteve ' + JSON.stringify(r) }); } catch(e) { results.push({ pass: false, message: 'Erro: ' + e.message }); }
