function __runTests() {
    const results = [];
    try { const r = seguro(() => 42); const exp = "42"; const pass = JSON.stringify(r) === JSON.stringify(exp); results.push({ pass, message: pass ? '42 ok!' : 'Incorreto: esperado ' + JSON.stringify(exp) + ' obteve ' + JSON.stringify(r) }); } catch(e) { results.push({ pass: false, message: 'Erro: ' + e.message }); }
    try { const r = seguro(() => { throw new Error('x') }); const exp = "'erro'"; const pass = JSON.stringify(r) === JSON.stringify(exp); results.push({ pass, message: pass ? 'Erro capturado!' : 'Incorreto: esperado ' + JSON.stringify(exp) + ' obteve ' + JSON.stringify(r) }); } catch(e) { results.push({ pass: false, message: 'Erro: ' + e.message }); }
    return results;
}
